tinymce.PluginManager.add("elfinder",function(editor){editor.settings.file_browser_callback=function(id,value,type,win){$('<div/>').dialogelfinder({url:'/file-manager-elfinder/connect',commandsOptions:{getfile:{oncomplete:'destroy'}},getFileCallback:function(obj){var fieldElm=win.document.getElementById(id);fieldElm.value=editor.convertURL(obj.url);if("fireEvent"in fieldElm){fieldElm.fireEvent("onchange")}else{var evt=document.createEvent("HTMLEvents");evt.initEvent("change",false,true);fieldElm.dispatchEvent(evt)}}})}},["elfinder/js"]);